import React from 'react';
import Card from './Card.jsx'

export default function Cards(props) {
  // acá va tu código
  // tip, podés usar un map
  return (
  <div>
    {props.cities.map(city => <Card 
    name = {city.name} 
    min = {city.main.temp_min} // cuidado con la forma en la que llega la info desde data.js
    max = {city.main.temp_max} 
    img = {city.weather[0].icon} // weather es un arreglo cuyo 1er elemnto es un objeto con todas sus propiedades
    onClose = {()=>alert(city.name)}/>)}
  </div>
  )
};
