  // ac치 va tu c칩digo

  const [city, setCity] = useState('')

  return (
  <div className={`${s.bar}`}>
    <input 
      className={`${s.input}`} 
      id='input' 
      type='text' 
      placeholder='Ciudad...' >

    </input>
    <button 
      className={`${s.button}`} 
      onClick={() => {onSearch(city); setCity('')}}>
        Agregar
    </button>
  </div>
  )
};
